import React, { Component,ReactNode ,View}  from 'react';
import {Flex,
  Box,
  FormControl,
  FormLabel,
  Input,
  Checkbox,
  Stack,
  Link,
  Button,
  Heading,
  Text,
  useColorModeValue
} from '@chakra-ui/react';


var form_values={
  'Name':'',
  'Date':'',
  'Time':'',
  'Location':'',
  'Pre-Requisites':'',
  'Prize Money':'',
  'Additional Information':'',
  'Poster/Image':'',
}

export default function Body (){
  const [name, setEmail] = React.useState("");
  const [date, setPassword] = React.useState("");
  const [time, settime] = React.useState("");
  const [location, setlocation] = React.useState("");
  const [prereq, setprereq] = React.useState("");
  const [prizemoney, setprizemoney] = React.useState("");
  const [addinfo, setaddinfo] = React.useState("");
  const [poster, setposter] = React.useState("");

  const handleSubmit = (event) => {
    console.log(`
      Name: ${name}
      Date: ${date}
      Time: ${time}
      Location: ${location}
      Pre-Requisites: ${prereq}
      Prize Money: ${prizemoney}
      Additional Information: ${addinfo}
      Poster/Image: ${poster}
      `);
      event.preventDefault();
    }
      
  return (
    <Flex
      minH={'100vh'}
      align={'center'}
      justify={'center'}
      bg={useColorModeValue('gray.50', 'gray.800')}>
      <Stack spacing={8} mx={'auto'} maxW={'lg'} py={12} px={6}>
        <Stack align={'center'}>
          <Heading fontSize={'4xl'}>Event Proposal Form</Heading>
          <Text fontSize={'lg'} color={'gray.600'}>
            What have you been cookingüçú
          </Text>
        </Stack>
        <Box
          rounded={'lg'}
          bg={useColorModeValue('white', 'gray.700')}
          boxShadow={'lg'}
          p={8}>
          <Stack spacing={4}>
            <FormControl id="name">
              <FormLabel>Name</FormLabel>
              <Input type="text" value={name} onChange={e => setname(e.target.value)} required/>
            </FormControl>
            <FormControl id="date">
              <FormLabel>Date</FormLabel>
              <Input type="date" />
            </FormControl>
            <FormControl id="time">
              <FormLabel>Time</FormLabel>
              <Input type="time" />
            </FormControl>
            <FormControl id="location">
              <FormLabel>Location</FormLabel>
              <Input type="text" />
            </FormControl>
            <FormControl id="prereqs">
              <FormLabel>Pre-Requisites</FormLabel>
              <Input type="text" />
            </FormControl>
            <FormControl id="prizemoney">
              <FormLabel>Prize Money</FormLabel>
              <Input type="number" />
            </FormControl>
            <FormControl id="additional-info">
              <FormLabel>Additional Information</FormLabel>
              <Input type="text" />
            </FormControl>
            <FormControl id="Poster">
              <FormLabel>Poster/Image</FormLabel>
              <Input type="file" />
            </FormControl>
            <Stack spacing={10}>
              <Button bg={'blue.400'} color={'white'} _hover={{   bg: 'blue.500', }} onClick={handleSubmit}>
                Submit
              </Button>
            {console.log(form_values)}
            </Stack>
          </Stack>
        </Box>
      </Stack>
    </Flex>
  )
}/*
