import React, { Component }  from 'react';
import {
    Box,
    Heading,
    Container,
    Text,
    Button,
    Stack,
    Icon,
    useColorModeValue,
    createIcon,
    ThemeProvider,
  CSSReset,
  theme,
} from '@chakra-ui/react';
import {useParams} from "react-router-dom";
import Body from './components/Body'
import Header from './components/Header'
import getData from './components/getData'
import getEvent from './components/getEvent'
import { useState, useEffect } from 'react';

/*
export default function Club_Page(){
  
  var name = useParams().name;

  const [event, setevent] = useState({});
  const [general, setgeneral] = useState({});
  
  useEffect( () => {
  async function fetchData() {
  console.log(name);
  console.log(name.slice(0,name.indexOf("@")));
  console.log(name.slice(name.indexOf("@")+1,));

  let eResponse_general  = await getData(name.slice(name.indexOf("@")+1,))
  //console.log(eResponse_general);
  //general = eResponse_general.data;
  setevent(eResponse_general.data)
  let eResponse_event  = await getEvent(name.slice(0,name.indexOf("@")))
  //console.log(eResponse_event);
  //event = eResponse_event.data;
  setevent(eResponse_event.data)


  }
  fetchData();
  });

  var alldata = {'name':"yaba","desc":"daba","email":"email"};
  return (
    <ThemeProvider theme={theme}>
    <CSSReset/>
    <Header Info={event}></Header>
    <Body Info={general}></Body>
  </ThemeProvider>
  )
}*/
class Club_Page extends React.Component {
  constructor() {
    super();
  }

  state = {
    clubs: [],
  };

  componentDidMount() {
    this.getClubs();
  }

  getClubs = () => {
    console.log("Getting Clubs");
    axios
      .get("http://127.0.0.1:8000/api/clubinfo")
      .then((data) => this.setState({ clubs: data.data }));
  };

  Redirect_Club = (name,id) => {
    console.log("Sending you to Club Page ------------->");
    console.log(name);
    console.log(id);
    history.push(`/club/${name}@${id}`)
  };

  render() {
    return (
      <div>

        {
        this.state.clubs.map((user, i) =>
        <Box bg="#fffff" border="2px" width="90%" ml="5%" mb="2px" flexDirection="row" onClick={() =>this.Redirect_Club(this.state.clubs[i]["name"],this.state.clubs[i]["id"])} key={this.state.clubs[i]["id"]}>
          <Flex display="flex" flexDirection="row">
            {this.state.clubs.length !== 0 ? (
              <Text ml={3} mt={3} mb={3} fontWeight="bold">
                {this.state.clubs[i]["club_name"]}
              </Text>
            ) : null}

            {this.state.clubs.length !== 0 ? (
              <Text ml={3} mt={3} mb={3} fontWeight="light">
                {this.state.clubs[i]["name"]}
              </Text>
            ) : null}

            <Image ml="70%" mt={3} size="5px" src="https://i.pinimg.com/originals/71/72/16/7172161b580470deb78078669236d2c1.jpg" width="25px" height="25px"/>
            <Image mt={3} size="5px" src="https://i.pinimg.com/originals/1d/ba/53/1dba53feeadf1a746f160396a6401135.png" width="25px" height="25px" />
          </Flex>
        </Box>
      )
      }
      </div>
    );
  }
}

export default Clubs;
