import React, { Component,ReactNode }  from 'react';
import {Flex,
    Box,
    Heading,
    Container,
    Text,
    Button,
    Stack,
    Icon,
    useColorModeValue,
    createIcon,
    SimpleGrid,
    Image,
} from '@chakra-ui/react';

import Head from 'next/head';

const Body = () => {
    return (
      <Box bg={'gray.800'} position={'relative'}>
      <Container maxW={'7xl'} zIndex={10} position={'relative'}>
      <Stack direction={{ base: 'row', lg: 'row' }}>
        <Stack>
          <Stack direction={{ base: 'column', lg: 'row' }}>
            <Stack
              flex={1}
              color={'gray.400'}
              justify={{ lg: 'center' }}
              py={{ base: 4, md: 20, xl: 5 }}>
              <Box mb={{ base: 8, md: 20 }}>
                <Text fontSize={'xl'} color={'white'}>
                  Event Description
                </Text>
              </Box>
              <SimpleGrid columns={{ base: 1, md: 2 }} spacing={10}>
                {stats.map((stat) => (
                  <Box key={stat.title}>
                    <Text
                      fontFamily={'heading'}
                      fontSize={'3xl'}
                      color={'white'}
                      mb={3}>
                      {stat.title}
                    </Text>
                    <Text fontSize={'xl'} color={'gray.400'}>
                      {stat.content}
                    </Text>
                  </Box>
                ))}
              </SimpleGrid>
            </Stack>
          </Stack>
          </Stack>
          <Stack paddingLeft="500">
          <Image
                src="https://i.pinimg.com/736x/43/29/c2/4329c2824fec2873b5e0569180e482cf.jpg"
                width="300px"
                height="500px"
              />
          </Stack>
          </Stack>
            <Flex flex={1} />
      </Container>
      
    </Box>
    )
}

const StatsText = ({ children }: { children: ReactNode }) => (
  <Text as={'span'} fontWeight={700} color={'white'}>
    {children}
  </Text>
);

const stats = [
  {
    title: 'Location',
    content: (
      <>
        <StatsText>Date Time</StatsText> Location
      </>
    ),
  },
  {
    title: 'Pre-Reqs',
    content: (
      <>
        <StatsText>None</StatsText> 
      </>
    ),
  },
  {
    title: 'Prize Money',
    content: (
      <>
        <StatsText>10k</StatsText> 
      </>
    ),
  },
  {
    title: 'Additional Info',
    content: (
      <>
        <StatsText>Nop</StatsText> 
      </>
    ),
  },
];
export default Body
