import {
  Flex,
  Box,
  Image,
  Badge,
  Text,
  Button,
  Icon,
  useColorModeValue,
  Stack,
} from "@chakra-ui/react";
import axios from "axios";
import { FcGoogle } from 'react-icons/fc';
import React from "react";
import GoogleButton from "./GLogout";

const Header = () => {
  return (
    <>
      <Flex display="flex" flexDirection="column" alignItems="center" justifyContent="center" textAlign="center" mt={4} >
        <Flex display="flex" flexDirection="row" alignItems="flex-start" justifyContent="flex-start" >
          <Text fontSize="3xl" fontWeight="bold">
            ⚡️Club Management Portal
          </Text>
          <Badge variant="subtle" variantColor="pink" ml={1}>
            BETA
          </Badge>
        </Flex>
        <Text color="gray.500">Sup</Text>
      </Flex>
      <Box bgGradient="linear(teal.500 55%, green.400 95%)" px={4}>
        <Flex h={16} alignItems={"center"} justifyContent={"space-between"}>
          <Text fontSize="xl" fontWeight="medium">
            Welcome, {window.sessionStorage.getItem("name")}
          </Text>
          <Button maxW={'xs'} variant={'outline'} leftIcon={<FcGoogle />} bg="white">
            <Text>Logout</Text>
        </Button>
        </Flex>
      </Box>
    </>
  );
};
class Club_Page extends React.Component {
  constructor() {
    super();
  }

  state = {
    user_info: {},
  };

  componentDidMount() {
    this.getUser();
    this.setinfo();
    
  }

  getUser = () => {
    console.log("Getting User");
    axios
      .get("http://127.0.0.1:8000/api/user/info")
      .then((data) => this.setState({ user_info: data.data }));

  };
  
  setinfo = () => {
    console.log("Setting User Info");
    window.sessionStorage.setItem('email', this.state.user_info.email);
    window.sessionStorage.setItem('name', this.state.user_info.name);
  };

  
  render() {
    return (
      <>
      <ThemeProvider theme={theme}>
        <CSSReset/>
        <Header></Header>
        <Events></Events>
        <Clubs></Clubs>
      </ThemeProvider>
      {console.log(this.state.user_info)}
      </>
    )
  }
}
export default Header;
