import React, { Component } from "react";
import {
  Box,
  Heading,
  Container,
  Text,
  Button,
  Stack,
  Icon,
  useColorModeValue,
  createIcon,
  ThemeProvider,
  CSSReset,
  theme,
} from "@chakra-ui/react";

import Body from "./components/Body";
import Header from "./components/Header";
import { useParams } from "react-router-dom";

export default function Events_Page() {

  const id = useParams().id;
  console.log(id);

  var name = useParams().name;

  const [event, setevent] = useState({});
  const [general, setgeneral] = useState({});
  
  useEffect( () => {
  async function fetchData() {
  let eResponse_general  = await getData(name.slice(name.indexOf("@")+1,))
  setgeneral(eResponse_general.data)

  let eResponse_event  = await getEvent(name.slice(0,name.indexOf("@")))
  setevent(eResponse_event.data)

  async function getEventInfo(eventID) {
    let res = await axios
      .get(`http://127.0.0.1:8000/api/clubs/${id}`)
      .then((data) => setEventInfo(data.data));
  }
  getEventInfo(eventID);
  }
  fetchData();
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <CSSReset />
      <Header Info={general}></Header>
      <Body eventid={id}></Body>
    </ThemeProvider>
  );
}
