import React, { useState } from "react";
import {
  ThemeProvider,
  CSSReset,
  theme,
  Box,
  Image,
  Badge,
  Text,
  Icon,
  Stack,
  Avatar,
  AvatarBadge,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  FormLabel,
  Input,
  FormHelperText,
  FormErrorMessage,
  Grid,
  Switch,
  InputGroup,
  InputRightElement,
  Flex,
  Tag,
  Heading,
  Button,
  useColorModeValue,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
} from "@chakra-ui/react";
import axios from "axios";
import history from "../../history";

class Events extends React.Component {
  constructor() {
    super();
  }

  state = {
    events: [],
    data : [
      { label: 'Total Subscribers', value: '71,887' },
      { label: 'Avg. Open Rate', value: '56.87%' },
      { label: 'Avg. Click Rate', value: '12.87%' },
    ]
  };
  componentDidMount() {
    this.getEvents();
  }

  getEvents = () => {
    console.log("Getting Events");
    axios
      .get("http://127.0.0.1:8000/api/clubs/unapproved")
      .then((data) => this.setState({ events: data.data }));
  };

  Redirect_Event = (name) => {
    console.log("Sending you to Event Page ------------->");
    history.push(`/event/${name}`);
  };

  
  
  render() {
    return (
      <>
      <SimpleGrid columns={{ base: 1, md: 3 }} spacing="6">
        {this.state.events.map((user, i) => (
          <Stat px={{ base: 4, sm: 6 }} py="5" bg={'gray.200'} shadow="base" rounded="lg">
          <StatLabel fontWeight="medium" isTruncated color={'gray'}>
            {"dssfdfdsf"}
          </StatLabel>
          <StatNumber fontSize="3xl" fontWeight="medium" color={'white'}>
            {"Fdsfdsfdsf"}
          </StatNumber>
        </Stat>
        ))}
      </SimpleGrid>
      <Box as="section" bg={'gray'} p="10">
      <Box maxW="7xl" mx="auto" px={{ base: '6', md: '8' }}>
        <SimpleGrid columns={{ base: 1, md: 3 }} spacing="6">
            <Stat px={{ base: 4, sm: 6 }} py="5" bg={'gray.200'} shadow="base" rounded="lg">
            <StatLabel fontWeight="medium" isTruncated color={'gray'}>
              {"dssfdfdsf"}
            </StatLabel>
            <StatNumber fontSize="3xl" fontWeight="medium" color={'white'}>
              {"Fdsfdsfdsf"}
            </StatNumber>
          </Stat>
        </SimpleGrid>
      </Box>
    </Box>
    </>
    );
  }
}
//useColorModeValue('gray.50', 'gray.800')
export default Events;

