import React from "react";
import {
  ThemeProvider,
  CSSReset,
  theme,
  Box,
  Image,
  Badge,
  Text,
  Icon,
  Stack,
  Avatar,
  AvatarBadge,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  FormLabel,
  Input,
  FormHelperText,
  FormErrorMessage,
  Grid,
  Switch,
  InputGroup,
  InputRightElement,
  Flex,
  Tag,
  Heading,
  SimpleGrid,
  Button,
} from "@chakra-ui/react";
import axios from "axios";
import history from './../../history';

class Clubs extends React.Component {
  constructor() {
    super();
  }

  state = {
    clubs: [],
  };

  componentDidMount() {
    this.getClubs();
  }

  getClubs = () => {
    console.log("Getting Clubs");
    axios
      .get("http://127.0.0.1:8000/api/clubinfo")
      .then((data) => this.setState({ clubs: data.data }));
  };

  Redirect_Club = (name,id) => {
    console.log("Sending you to Club Page ------------->");
    console.log(name);
    console.log(id);
    history.push(`/club/${name}@${id}`)
  };

  render() {
    return (
      <>
      <SimpleGrid columns={1} p={5} gap={6}>
        {
          this.state.clubs.map((user, i) =>
          <Button justifyContent="flex-start">
              {console.log(this.state.clubs[i])}
              <Text>
                {this.state.clubs[i]["name"]}
              </Text>
              <Text ali="flex-end">
                {this.state.clubs[i]["name"]}
              </Text>
            </Button>
            )}
        </SimpleGrid>
      </>
    );
  }
}

export default Clubs;
/*
