import React, { useState } from "react";
import {
  ThemeProvider,
  CSSReset,
  theme,
  Box,
  Image,
  Badge,
  Text,
  Icon,
  Stack,
  Avatar,
  AvatarBadge,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  FormLabel,
  Input,
  FormHelperText,
  FormErrorMessage,
  Grid,
  Switch,
  InputGroup,
  InputRightElement,
  Flex,
  Tag,
  Heading,
  Button,
  useColorModeValue,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
} from "@chakra-ui/react";
import axios from "axios";
import {
  IoAlarmOutline,
} from 'react-icons/io5';
import history from "../../history";


export const Feat = ({ title, children, icon, toGo }: FeatProps) => {
  return (
    <Stack spacing="6" direction={{ base: 'column', md: 'row' }}>
      <Box fontSize="2xl">{icon}</Box>
      <Stack spacing="1">
        <Text fontWeight="extrabold" fontSize="lg">
      <Button onClick={()=>history.push(`/event/${toGo}`)}>
          {title}
      </Button>
        </Text>
        <Box color={useColorModeValue('gray.600', 'gray.400')}>{children}</Box>
      </Stack>
    </Stack>
  )
}

class Events extends React.Component {
  constructor() {
    super();
  }

  state = {
    events: [],
  };
  componentDidMount() {
    this.getEvents();
  }

  getEvents = () => {
    console.log("Getting Events");
    axios
      .get("http://127.0.0.1:8000/api/clubs/unapproved")
      .then((data) => this.setState({ events: data.data }));
  };

  Redirect_Event = (name) => {
    console.log("Sending you to Event Page ------------->");
    history.push(`/event/${name}`);
  };

  
  
  render() {
    return (
      <>
      <Grid p={10} gap={6} templateColumns="repeat(auto-fit, minmax(350px, 1fr))">
        {this.state.events.map((user, i) => (
        <Stat px={{ base: 4, sm: 6 }} py="5" bg={'gray.200'} shadow="base" rounded="lg" key={this.state.events[i]["club_name"]}>
          <StatLabel fontWeight="medium" isTruncated color={'gray'}>
            {this.state.events[i]["club_name"]}
          </StatLabel>
        <StatNumber fontSize="3xl" fontWeight="medium" color={'black'}>
            <Button onClick={() => this.Redirect_Event(this.state.events[i]["id"])} >
            {this.state.events[i]["name"]}
            </Button>
          </StatNumber>
          <Stack direction={'row'} align={'center'}>
            <Flex w={8} h={8} align={'center'} justify={'center'} rounded={'full'} bg={('yellow.100', 'yellow.900')}> 
              {IoAlarmOutline}
            </Flex>
            <Text fontWeight={600}>
              {"lol"}</Text>
          </Stack>
        </Stat>
        ))}
      </Grid>
    </>
    );
  }
}
