import React, { useState } from "react";
import {
  ThemeProvider,
  CSSReset,
  theme,
  Box,
  Image,
  Badge,
  Text,
  Icon,
  Stack,
  Avatar,
  AvatarBadge,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
  FormLabel,
  Input,
  FormHelperText,
  FormErrorMessage,
  Grid,
  Switch,
  InputGroup,
  InputRightElement,
  Flex,
  Tag,
  Heading,
  Button,
  useColorModeValue,
  SimpleGrid,
  Stat,
  StatLabel,
  StatNumber,
  useColorMode,
} from "@chakra-ui/react";
import axios from "axios";
import {
  IoAlarmOutline,
} from 'react-icons/io5';
import history from "../../history";

interface FeatureProps {
  text: string;
  c1: string;
  c2: string;
  c3: string;
}
const Feature = ({ text, c1, c2 }: FeatureProps) => {
  return (
    <Stack direction={'row'} align={'center'}>
            <Flex w={8} h={8} align={'center'} justify={'center'} rounded={'full'} bg={useColorModeValue(c1,c2)}> 
              <Icon as={IoAlarmOutline} color={c3} w={5} h={5}/>
            </Flex>
            <Text fontWeight={600}>
              {"lol"}
            </Text>
          </Stack>
  );
};


class Events extends React.Component {
  constructor() {
    super();
  }

  state = {
    events: [],
  };
  componentDidMount() {
    this.getEvents();
  }

  getEvents = () => {
    console.log("Getting Events");
    axios
      .get("http://127.0.0.1:8000/api/clubs/unapproved")
      .then((data) => this.setState({ events: data.data }));
  };

  Redirect_Event = (name) => {
    console.log("Sending you to Event Page ------------->");
    history.push(`/event/${name}`);
  };
  
  
  render() {
    return (
      <>
      <Grid p={10} gap={6} templateColumns="repeat(auto-fit, minmax(350px, 1fr))">
        {this.state.events.map((user, i) => (
        <Stat px={{ base: 4, sm: 6 }} py="5" bg={'gray.200'} shadow="base" rounded="lg" key={this.state.events[i]["club_name"]}>
          <StatLabel fontWeight="medium" isTruncated color={'gray'}>
            {this.state.events[i]["club_name"]}
          </StatLabel>
        <StatNumber fontSize="3xl" fontWeight="medium" color={'black'}>
            <Button onClick={() => this.Redirect_Event(this.state.events[i]["id"])} >
            {this.state.events[i]["name"]}
            </Button>
          </StatNumber>
          <Feature  text="lol" c1="green.100" c2="green.900"/>
        </Stat>
        ))}
      </Grid>
    </>
    );
  }
}
//useColorModeValue('gray.50', 'gray.800')
export default Events;

